const btn=document.querySelector(".button"),quote=document.querySelector(".quote-text"),number=document.querySelector(".NumberAdvice");async function getAdvice(){try{let e={method:"GET",cache:"no-cache"};const t="https://api.adviceslip.com/advice",c=await fetch(t,e),n=await c.json(),{advice:o}=n.slip;quote.textContent=`"${o}"`;const{id:i}=n.slip;number.textContent=`ADVICE #${i}`}catch(e){console.log(e)}}btn.addEventListener("click",getAdvice);